import React from 'react';
import './App.css';
import SideScroll from './Components/SideScroll.js';
import Grid from './Components/gridHeadlines.js';
import Footer from './Components/footer.js';

function App() {
  return (
    <div className="App">
    <Body />
    <Footer />
    </div>
  );
}

export default App;


class Body extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isLoaded: false,
      topHeadlines: [],
      business: [],
      technology: []
    }; 
  }


  componentDidMount () {
    fetch('https://newsapi.org/v2/top-headlines?country=us&pageSize=40&apiKey=5541ada8d67e408daa5de792ba9f50f4')
      .then(res => res.json())
      .then(
        (top) => {
        this.setState({
          isLoaded: true,
          topHeadlines: top.articles
        });
      })
    fetch('https://newsapi.org/v2/top-headlines?country=us&category=business&pageSize=20&apiKey=5541ada8d67e408daa5de792ba9f50f4')
      .then(res => res.json())
      .then(
        (bus) => {
          this.setState({
            isLoaded: true,
            business: bus.articles
          });
      })
      fetch('https://newsapi.org/v2/top-headlines?country=us&category=technology&pageSize=20&apiKey=5541ada8d67e408daa5de792ba9f50f4')
      .then(res => res.json())
      .then(
        (tech) => {
          this.setState({
            isLoaded: true,
            technology: tech.articles
          });
        }
      )
    }

  render() {
    const { error, isLoaded, topHeadlines, business, technology } = this.state;
    console.log(topHeadlines)
    console.log(business)
    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
      return <div className="loading">Loading...</div>;
    } else {
      return (
      <div>

          <h1>Top US Headlines</h1>
          <hr />
        <div className="grid-container">
          {topHeadlines.slice(0,13).map((item, index) => 
            <Grid key={index} className={index} titleA={item.title} storyA={item.urlToImage} />
          )}   
        </div>

          <h1>Top Business Headlines</h1>
          <hr />
        <div className="slider-container">
          {business.map((item, index) =>
            <SideScroll key={index} titleB={item.title} businessArticles={item.urlToImage} />
          )}
        </div>

          <h1>Top US Headlines <i className="smallText">Continued...</i></h1>
          <hr />
        <div className="grid-container">
          {topHeadlines.slice(14,27).map((item, index) => 
            <Grid key={index} className={index} titleA={item.title} storyA={item.urlToImage} />
          )}   
        </div>

          <h1>Top Technology Headlines</h1>
          <hr />
        <div className="slider-container">
        {technology.map((item, index) =>
           <SideScroll key={index} titleB={item.title} businessArticles={item.urlToImage} />
        )}
       </div>

      </div>
      );
    }
  }
}
