import React from 'react';
import './App.css';
import Scroll from './Components/Scroll.js'
import SideScroll from './Components/SideScroll.js'

function App() {
  return (
    <div className="App">
    <Json />
    </div>
  );
}

export default App;


class Json extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isLoaded: false,
      articles: [],
      business: []
    }; 
  }


  componentDidMount () {
    fetch('https://newsapi.org/v2/top-headlines?country=us&pageSize=40&apiKey=5541ada8d67e408daa5de792ba9f50f4')
      .then(res => res.json())
      .then(
        (result) => {
        this.setState({
          isLoaded: true,
          articles: result.articles
        });
      })
    fetch('https://newsapi.org/v2/top-headlines?country=us&category=business&apiKey=5541ada8d67e408daa5de792ba9f50f4')
      .then(res => res.json())
      .then(
        (post) => {
          this.setState({
            isLoaded: true,
            business: post.articles
          });
        }
      )
    }

  render() {
    const { error, isLoaded, articles, business } = this.state;
    console.log(Date())
    console.log(articles)
    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
      return <div className="loading">Loading...</div>;
    } else {
      return (
        <div>
        <div className="grid-container">
          {articles.slice(0,13).map((item, index) => 
            <Post key={index} className={index} titleA={item.title} storyA={item.urlToImage} />
          )}   
        </div>
        <div className="slider-container">
          {business.map((item, index) =>
             <SideScroll key={index} titleB={item.title} businessArticles={item.urlToImage} />
          )}
          </div>
        <div className="grid-container">
          {articles.slice(14,27).map((item, index) => 
            <Post key={index} className={index} titleA={item.title} storyA={item.urlToImage} />
          )}   
        </div>
        </div>
      );
    }
  }
}

const Post = (props) => {
    return(
      <div className={props.className}>
          <article className={props.className}>
          <div className="title">{props.titleA}</div>
          <img className="gridImg" src={props.storyA} alt="News-Story-Left" />
          </article>
      </div>
    );
}



const Header = (props) => {
    return(
      <div>
      </div>
    );
}
