import React from 'react';
import './App.css';
import Scroll from './Components/Scroll.js'

function App() {
  return (
    <div className="App">
    <Json />
    </div>
  );
}

export default App;


class Json extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isLoaded: false,
      articles: []
    };
  }


  componentDidMount () {
    fetch('https://newsapi.org/v2/top-headlines?country=us&apiKey=5541ada8d67e408daa5de792ba9f50f4')
      .then(res => res.json())
      .then(
        (result) => {
        this.setState({
          isLoaded: true,
          articles: result.articles
        });
      });
    }

  render() {
    const { error, isLoaded, articles } = this.state;
    console.log(articles)
    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
      return <div className="loading">Loading...</div>;
    } else {
      return (
        <InfiniteScroll
      dataLength={this.articles.length} //This is important field to render the next data
      next={this.componentDidMount}
      hasMore={true}
      loader={<h4>Loading...</h4>}
      endMessage={
        <p style={{textAlign: 'center'}}>
        <b>Come back later for more News Your Way!</b>
        </p>
      }
      // below props only if you need pull down functionality
      refreshFunction={this.refresh}
      pullDownToRefresh
      pullDownToRefreshContent={
      <h3 style={{textAlign: 'center'}}>&#8595; Pull down to refresh</h3>
      }
      releaseToRefreshContent={
      <h3 style={{textAlign: 'center'}}>&#8593; Release to refresh</h3>
      }>
        <div className="grid-container">
          {articles.map((item, index) => 
            <Post key={index} className={index} titleA={item.title} storyA={item.urlToImage} />
          )};   
        </div>
        </InfiniteScroll>

      );
    }
  }
}

const Post = (props) => {
    return(
      <div className={props.className}>
          <article className={props.className}>
          <div className="title">{props.titleA}</div>
          <img src={props.storyA} alt="News-Story-Left" />
          </article>
      </div>
    );
}

const Header = (props) => {
    return(
      <div>
      </div>
    );
}
