import React from 'react';
import TimeAgo from 'timeago-react';
import ModalPage from './modal.js';
import { Button, Modal } from 'react-bootstrap';

class Grid extends React.Component {
  constructor(props, context){
    super(props, context);
    this.state = {
      show: false,
    };

    this.handleShow = () => {
      this.setState({ show: true });
    };

    this.handleHide = () => {
      this.setState({ show: false });
    };
  }
    render() {
    return (

  <div className={props.className}>
    <article className={props.className}>
      <div className="title"><span>{props.titleA}</span></div>
        <div className="content-overlay"></div>
          <img className="content-image gridImg" src={props.storyA} alt="News-Story-Left" />
          <TimeAgo className="timeStamp" datetime={props.time} />
          <div className="content-details fadeIn-bottom fadeIn-right">
        <button className="btn btn-primary" onClick={this.state.show}>Read Full Story</button>

      </div>
    </article>
  </div>
    );
}
}

export default Grid;
