import React from "react";
import TimeAgo from "timeago-react";
import { Modal } from "react-bootstrap";
import LazyLoad from "react-lazyload";

const TopStories = props => {
  return (
    <div>
        <div className="top-main">
          <img src={props.image} onClick={props.click} />
          <p>{props.title}</p>
        </div>
        <TimeAgo className="timeStamp timeStampR" datetime={props.time} />
        <div>
          <div className="content-details fadeIn-bottom fadeIn-right">
            <button className="btn btn-primary" onClick={props.click}>
              Read Snippet
            </button>
            <Modal
              open={props.A}
              close={props.B}
              dialogClassName="modal-90w"
              aria-labelledby="example-custom-modal-styling-title"
            >
              <Modal.Header closeButton>
                <Modal.Title
                  id="example-custom-modal-styling-title"
                  className="snipHeader"
                >
                  {props.header}
                </Modal.Title>
              </Modal.Header>
              <Modal.Body className="snippet">
                {props.snippet}
                <hr />
                <a
                  className="url"
                  href={props.link}
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  View Full Story
                </a>
              </Modal.Body>
            </Modal>
          </div>
        </div>
        </div>
  );
};

export default TopStories;
