import React from 'react';
import TimeAgo from 'timeago-react';
import ModalPage from './modal.js';
import { Button, Modal } from 'react-bootstrap';

class Grid extends React.Component {
  constructor(props, context){
    super(props, context);
    this.state = {
      show: false,
    };

    this.handleShow = () => {
      this.setState({ show: true });
    };

    this.handleHide = () => {
      this.setState({ show: false });
    };
  }
    render() {
    return (

  <div className={props.className}>
    <article className={props.className}>
      <div className="title"><span>{props.titleA}</span></div>
        <div className="content-overlay"></div>
          <img className="content-image gridImg" src={props.storyA} alt="News-Story-Left" />
          <TimeAgo className="timeStamp" datetime={props.time} />
          <div className="content-details fadeIn-bottom fadeIn-right">
        <button className="btn btn-primary" onClick={this.handleShow}>Read Full Story</button>

        <Modal
          show={this.state.show}
          onHide={this.handleHide}
          dialogClassName="modal-90w"
          aria-labelledby="example-custom-modal-styling-title">
          <Modal.Header closeButton>
            <Modal.Title id="example-custom-modal-styling-title">
              Custom Modal Styling
            </Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <p>
              Ipsum molestiae natus adipisci modi eligendi? Debitis amet quae
              unde commodi aspernatur enim, consectetur. Cumque deleniti
              temporibus ipsam atque a dolores quisquam quisquam adipisci
              possimus laboriosam. Quibusdam facilis doloribus debitis! Sit
              quasi quod accusamus eos quod. Ab quos consequuntur eaque quo rem!
              Mollitia reiciendis porro quo magni incidunt dolore amet atque
              facilis ipsum deleniti rem!
            </p>
          </Modal.Body>
        </Modal>

      </div>
    </article>
  </div>
    );
}
}

export default Grid;
