import React from 'react';
import './App.css';

function App() {
  return (
    <div>
      <Json />
    </div>
  );
}

export default App;


class Parent extends React.Component {
  constructor(props){
    super(props);
      this.state = {
        isLightOn: true
      };

      this.handleClick = this.handleClick.bind(this);
  };

  handleClick(){
    this.setState(prevState => ({
        isLightOn: !prevState.isLightOn
    }));
  }


  render() {
    return(
      <div>
      <button onClick={this.handleClick}>Click Me</button>
      <Child input={this.state.isLightOn ? 'What' : 'The'} />
      </div>
      );
    }
}


const Child = (props) => {
  return(
    <div>
    <h1>{props.input}</h1>
    <h2>{props.input}</h2>
    </div>
    );
};


class Json extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      items: []
    };
  }

  componentDidMount() {
    const ApiKey = '5541ada8d67e408daa5de792ba9f50f4';
    fetch('https://newsapi.org/v2/top-headlines?country=us&apiKey=' + ApiKey )
      .then(res => res.json())
      .then(
        (result) => {
          this.setState({
            isLoaded: true,
            items: result
          });
          console.log(result.articles[0])
        },
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      )
  }

  render() {
    const { error, isLoaded, items } = this.state;
    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
      return <div>Loading...</div>;
    } else {
      return (
        <div>
          <header>
            <h1 className="header">Your News</h1>
                <p>Your Way!</p>
              <hr />
          </header>
            <Post storyA={items.articles[0].urlToImage} storyB={items.articles[1].urlToImage} storyC={items.articles[2].urlToImage} />
        </div>
      );
    }
  }
}

const Post = (props) => {
    return(
        <div className="grid-container">

          <div>
            <img className="First"
            src={props.storyA} alt="News-Story-Left" />
          </div>

          <div >
            <img className="First"
            src={props.storyB} alt="News-Story-Right" />
          </div>

          <div >
            <img className="First"
            src={props.storyC} alt="News-Story-Bottom" />
            </div>
        </div>
    );
}
