import React from 'react';
import './App.css';
import Scroll from './Components/Scroll.js'

function App() {
  return (
    <div>
      <Scroll />
    </div>
  );
}

export default App;


class Parent extends React.Component {
  constructor(props){
    super(props);
      this.state = {
        isLightOn: true
      };

      this.handleClick = this.handleClick.bind(this);
  };

  handleClick(){
    this.setState(prevState => ({
        isLightOn: !prevState.isLightOn
    }));
  }


  render() {
    return(
      <div>
      <button onClick={this.handleClick}>Click Me</button>
      <Child input={this.state.isLightOn ? 'What' : 'The'} />
      </div>
      );
    }
}


const Child = (props) => {
  return(
    <div>
    <h1>{props.input}</h1>
    <h2>{props.input}</h2>
    </div>
    );
};


class Json extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      items: []
    };
  }

  componentDidMount() {
    const ApiKey = '5541ada8d67e408daa5de792ba9f50f4';
    fetch('https://newsapi.org/v2/top-headlines?country=us&apiKey=' + ApiKey )
      .then(res => res.json())
      .then(
        (result) => {
          this.setState({
            isLoaded: true,
            items: result
          });
          console.log(result.articles[0].publishedAt)
        },
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      )
  }

  render() {
    const { error, isLoaded, items } = this.state;
    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
      return <div>Loading...</div>;
    } else {
      return (
        <div>
          <header>
            <h2 className="header">Your News</h2>
                <p>Your Way!</p>
              <hr />
          </header>
          {items.map(obj => {
            <Scroll storyA={obj}/>
          })}  
          }
        </div>
      );
    }
  }
}

const Post = (props) => {
    return(
      <div>

        <div className="grid-container">

          <article className="First">
          <div className="title">{props.titleA}</div>
          <img
          src={props.storyA} alt="News-Story-Left" />
          </article>

          <article className="Second">
          <div className="title">{props.titleB}</div>
          <img
          src={props.storyB} alt="News-Story-Right" />
          </article>

          <article className="Third">
          <div className="title">{props.titleC}</div>
          <img
          src={props.storyC} alt="News-Story-Bottom" />
          </article>
        </div>

        <div className="grid-container">

          <article className="First">
          <div className="title">{props.titleD}</div>
          <img
          src={props.storyD} alt="News-Story-Left" />
          </article>

          <article className="Second">
          <div className="title">{props.titleE}</div>
          <img
          src={props.storyE} alt="News-Story-Right" />
          </article> 

          <article className="Third">
          <div className="title">{props.titleF}</div>
          <img 
          src={props.storyF} alt="News-Story-Bottom" />
           </article> 
        </div>

      </div>
    );
}
