import React from 'react';
import './App.css';

function App() {
  return (
    <div>
      <h1>Lehgo</h1>
      <Parent />
      <Json />
    </div>
  );
}

export default App;


class Parent extends React.Component {
  constructor(props){
    super(props);
      this.state = {
        isLightOn: true
      };

      this.handleClick = this.handleClick.bind(this);
  };

  handleClick(){
    this.setState(prevState => ({
        isLightOn: !prevState.isLightOn
    }));
  }


  render() {
    return(
      <div>
      <button onClick={this.handleClick}>Click Me</button>
      <Child input={this.state.isLightOn ? 'What' : 'The'} />
      </div>
      );
    }
}


const Child = (props) => {
  return(
    <div>
    <h1>{props.input}</h1>
    <h2>{props.input}</h2>
    </div>
    );
};


class Json extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      items: []
    };
  }

  componentDidMount() {
    fetch('https://jsonplaceholder.typicode.com/posts')
      .then(res => res.json())
      .then(
        (result) => {
          this.setState({
            isLoaded: true,
            items: result
          });
          console.log(result)
        },
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      )
  }

  render() {
    const { error, isLoaded, result } = this.state;
    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
      return <div>Loading...</div>;
    } else {
      return (
          <Post />
      );
    }
  }
}


const Post = (props) => {
  return(
        <ul>
          <li>{props.item}</li>
        </ul>
    );
}
