import React from "react";
import "../Assets/globaljs.js";
import { Navbar, Nav } from "react-bootstrap";


class Header extends React.Component {
		constructor(props, context) {
	    super(props, context);
	    this.state = {
	      user: ''
	    };
	}

	 timeOfDay () {

		let time = new Date().getHours();
		let greeting;

		switch (true) {
			case time < 4:
				return (greeting = "Sup ");
				break;
			case time > 3 && time < 12:
				return (greeting = "Morning ");
				break;
			case time > 11 && time < 17:
				return (greeting = "Afternoon ");
				break;
			case time > 16 && time < 21:
				return (greeting = "Evening ");
				break;
			case time > 20 && time < 25:
				return (greeting = "GoodNight ");
				break;
			default:
				break;
		}
	};


	input () {
		let getUser = localStorage.getItem("visted");
		let user =  getUser;

		if (user === null || user === 'null') {
			user = prompt("Whats your name?");
			localStorage.setItem("visted", user);

		}
		this.forceUpdate()
		return user;
	}
	render () {

		return (
		<Navbar bg="dark" variant="dark" sticky="top">
			<Nav>
				<aside className="greeting">
					<div>
						<span>{this.timeOfDay()}
						</span>
							<span className="userName">
								{user === 'null' || user === null ? 
								<button className='btn-warning' 
										onClick={input}>Update name
								</button> : user}
							</span>
					</div>
				</aside>

				<aside className="nav-search">
					<input type="text" placeHolder="Search coming soon!" />
				</aside>
			</Nav>
		</Navbar>
		)
	};
}
export default Header;
