import React from 'react';
import './App.css';
import SideScroll from './Components/SideScroll.js';
import Grid from './Components/gridHeadlines.js';
import ModalPage from './Components/modal.js';
import Footer from './Components/footer.js';
import { Button, Modal } from 'react-bootstrap';

function App() {
  return (
    <div className="App">
    <Body />
    <Footer />
    </div>
  );
}

export default App;

class Body extends React.Component {
  constructor(props, context) {
    super(props, context);
    this.state = {
      isLoaded: false,
      topHeadlines: [],
      business: [],
      technology: [],
      sports: [],
      show: false
    }; 
    this.handleShow = () => {
      this.setState({ show: true });
    };

    this.handleHide = () => {
      this.setState({ show: false });
    };
  }

componentDidMount () {
  const ApiKey = '5541ada8d67e408daa5de792ba9f50f4'
  fetch('https://newsapi.org/v2/top-headlines?country=us&pageSize=40&apiKey='+ ApiKey)
    .then(res => res.json())
    .then(
      (top) => {
        this.setState({
         isLoaded: true,
         topHeadlines: top.articles
      });
    })
  fetch('https://newsapi.org/v2/top-headlines?country=us&category=business&pageSize=20&apiKey='+ ApiKey)
    .then(res => res.json())
    .then(
      (bus) => {
        this.setState({
          isLoaded: true,
          business: bus.articles
        });
    })
    fetch('https://newsapi.org/v2/top-headlines?country=us&category=technology&pageSize=20&apiKey='+ ApiKey)
    .then(res => res.json())
    .then(
      (tech) => {
        this.setState({
          isLoaded: true,
          technology: tech.articles
        });
      }
    )
    fetch('https://newsapi.org/v2/top-headlines?country=us&category=sports&pageSize=20&apiKey='+ ApiKey)
    .then(res => res.json())
    .then(
      (sport) => {
        this.setState({
          isLoaded: true,
          sports: sport.articles
        });
      }
    )
  }

  render() {
    const { error, isLoaded, topHeadlines, business, technology, sports } = this.state;
    console.log(topHeadlines)
    console.log(business)
    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
      return <div className="loading">Loading...</div>;
    } else {
      return (
      <div>

      <h1 className="subHeader">Top US Headlines</h1>
      <hr />
        <div className="grid-container">
          {topHeadlines.slice(0,13).map((item, index) => 
            <Grid key={index} 
                  className={index} 
                  titleA={item.title} 
                  storyA={item.urlToImage} 
                  time={item.publishedAt}
                  content={this.handleShow} 
                  A={this.state.show}
                  B={this.handleHide}
                  contentB={item.content} />
                  )}</div>

      <h1 className="subHeader">Top Business Headlines</h1>
      <hr />
        <div className="slider-container">
          {business.map((item, index) =>
            <SideScroll key={index} 
                      titleB={item.title} 
                      businessArticles={item.urlToImage} 
                      time={item.publishedAt} 
                      content={this.handleShow}
                      A={this.state.show}
                      B={this.handleHide}
                      contentB={item.content} />
                      )}</div>

      <h1 className="subHeader">Top US Headlines&nbsp;<i className="smallText">Continued...</i></h1>
      <hr />
        <div className="grid-container-reverse">
          {topHeadlines.slice(14,27).map((item, index) => 
            <Grid key={index} 
                  className={index} 
                  titleA={item.title} 
                  storyA={item.urlToImage} 
                  time={item.publishedAt} 
                  content={this.handleShow} />
                  )}</div>

      <h1 className="subHeader">Top Technology Headlines</h1>
      <hr />
        <div className="slider-container">
          {technology.map((item, index) =>
            <SideScroll key={index}
            titleB={item.title} 
            businessArticles={item.urlToImage} 
            time={item.publishedAt} 
            content={this.handleShow} />
                  )}</div>

      <h1 className="subHeader">Top US Headlines&nbsp;<i className="smallText">Continued...</i></h1>
      <hr />
        <div className="grid-container">
          {topHeadlines.slice(28,38).map((item, index) => 
            <Grid key={index} 
            className={index} 
            titleA={item.title} 
            storyA={item.urlToImage} 
            time={item.publishedAt} 
            content={this.handleShow} />
            )}</div>

      <h1 className="subHeader">Top Sports Headlines</h1>
      <hr />
      <div className="slider-container">
      {sports.map((item, index) =>
      <SideScroll key={index} titleB={item.title} businessArticles={item.urlToImage} time={item.publishedAt} content={this.handleShow} contentB={item.content} />
      )}
      </div>

      </div>
      );
    }
  }
}
