import React from 'react';
import './App.css';
import Scroll from './Components/Scroll.js'

function App() {
  return (
    <div>
    <Json />
    </div>
  );
}

export default App;


class Parent extends React.Component {
  constructor(props){
    super(props);
      this.state = {
        isLightOn: true
      };

      this.handleClick = this.handleClick.bind(this);
  };

  handleClick(){
    this.setState(prevState => ({
        isLightOn: !prevState.isLightOn
    }));
  }


  render() {
    return(
      <div>
      <button onClick={this.handleClick}>Click Me</button>
      <Child input={this.state.isLightOn ? 'What' : 'The'} />
      </div>
      );
    }
}


const Child = (props) => {
  return(
    <div>
    <h1>{props.input}</h1>
    <h2>{props.input}</h2>
    </div>
    );
};


class Json extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isLoaded: false,
      articles: []
    };
  }


  componentDidMount () {
    fetch('https://newsapi.org/v2/top-headlines?country=us&apiKey=5541ada8d67e408daa5de792ba9f50f4')
      .then(res => res.json())
      .then(
        (result) => {
        this.setState({
          isLoaded: true,
          articles: result.articles
        });
      });
    }

  render() {
    const { error, isLoaded, articles } = this.state;
    const classSet = [1]
    console.log(articles)
    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
      return <div>Loading...</div>;
    } else {
      return (
        <div className="grid-container">
          {articles.map((item, index) => 
            <Post key={index} className={classSet.map(set => set++)} titleA={item.title} storyA={item.urlToImage} />
          )};   
        </div>
      );
    }
  }
}



const Post = (props) => {
    return(
      <div>

          <article className={props.className}>
          <div className="title">{props.titleA}</div>
          <img src={props.storyA} alt="News-Story-Left" />
          </article>
      </div>
    );
}
